import React from 'react';

class AddUser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            firstName: '',
            lastName: '',
            bio: '',
            age: 2,
            isHappy: false,
        }
    }
    render() {
        return (
            <form>
                <input type="text" placeholder='Имя' onChange={(e) => this.setState({firstName: e.target.value})}/>
                <input type="text" placeholder='Фамилия' onChange={(e) => this.setState({lastName: e.target.value})}/>
                <textarea name="Биография" id="bio" cols="30" rows="5" placeholder='Биография' onChange={(e) => this.setState({bio: e.target.value})}></textarea>
                <input type="text" placeholder='Возраст' onChange={(e) => this.setState({age: e.target.value})}/>
                <label htmlFor="isHappy">Счастив?</label>
                <input type="checkbox" id='isHappy' placeholder='Имя' onChange={(e) => this.setState({isHappy: e.target.checked})}/>
                <button type='button' onClick={() => this.props.onAdd({
                    firstName: this.state.firstName,
                    lastName: this.state.lastName,
                    bio: this.state.bio,
                    age: this.state.age,
                    isHappy: this.state.isHappy,
                })}>Добавить</button>
            </form>
        )
    }
}

export default AddUser;
